operation_script.value:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    let {
        sysId
    } = request.body.data;
    let title = 'People in this List...';
    let encodedQuery = '';
    let count = 0;
    let type = '';
    let view = '';
    let orderByField = '';
    let table = '';
    let staticView = '';
    let filters = [];
    const query = {};

    const audienceGr = new GlideRecord('x_cadso_automate_audience');
    if (audienceGr.get(sysId)) {
        title = audienceGr.getDisplayValue('source') + 's in this List';
        encodedQuery = audienceGr.getDisplayValue('conditions');
        type = audienceGr.getDisplayValue('type');
        count = audienceGr.getValue('count');
        view = audienceGr.getValue('view');
        staticView = audienceGr.getValue('static_view');
        orderByField = audienceGr.getValue('order_by');
        table = audienceGr.getDisplayValue('table');
        filters = JSON.parse(audienceGr.getValue('filter_object')) || [];

    }

    const isStatic = type === 'Static';

    if (isStatic) {
        var hashEncodedQuery = '';
        var audHashGR = new GlideRecord('x_cadso_automate_audience_hash');
        audHashGR.addQuery('audience', sysId);
        audHashGR.setLimit(10);
        audHashGR.query();

        while (audHashGR.next()) {
            var hashArr = JSON.parse(audHashGR.getValue('member_hash')) || [];
            var idArr = [];

            for (var x = 0; x < hashArr.length; x++) {
                if (!hashArr[x]) {
					//Do nothing
                } else {
                    idArr.push(hashArr[x].sysId);
                }

            }
            if (idArr.length > 0) {
                hashEncodedQuery += 'sys_idIN' + idArr.join(',');
            }
        }
    }
    response.setBody({
        title,
        encodedQuery,
        type,
        filters,
        query,
        count,
        table,
        view,
        orderByField,
        staticObj: isStatic ? {
            orderByField: 'name',
            table,
            encodedQuery: hashEncodedQuery,
            view: staticView
        } : {},
        isStatic
    });


})(request, response);

