(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var data = request.body.data;
	var type = data.type || '';
	var sysId = data.sysId || '';
	var table = data.table || '';

	var newBody = {
        type: type,
        success: false,
        sysId: sysId,
        table: table,
    };

	var gr = new GlideRecord(table);
	if (gr.get(sysId)) {
		if (type === "createDKIM") {
			var selector = data.selector;
			var bits = data.bits;
			// call script include to trigger flow
			var CreateDKIMKeyApi = new x_cadso_automate.CreateDKIMKey();
			CreateDKIMKeyApi.triggerFlow({ domainSysId: sysId, selector, bits });
			
			newBody['success'] = true;
			newBody['selector'] = selector;
			newBody['bits'] = bits;
		}
	}

	response.setBody(newBody);

})(request, response);

