(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    let apiUtil = new x_cadso_automate.APIUtils();
	
	try{
		let table = 'x_cadso_automate_content_template';
		let sysID = request.pathParams.sys_id;
		let queryParams = request.queryParams;
		let returnFields = request.queryParams.fields ? request.queryParams.fields[0].split(',') : null;
		let template = apiUtil.get({ table, sysID, returnFields });
		let selectedBrandKit = {};
		if (template.brand_kit) {
			const brandKitApi = new x_cadso_core.BrandKitAPI();
			selectedBrandKit = brandKitApi._getBrandKit({ sysId: template.brand_kit });
		}
		if(!template){
			return new sn_ws_err.NotFoundError('Could not find template');
		}
		response.setBody({ ...template, selectedBrandKit });
	}catch(error){
		return new sn_ws_err.BadRequestError(error.message);
	}


})(request, response);

