operation_script.value:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    const {
        table
    } = request.queryParams;
    const tableGr = new GlideRecord(table);
    if (tableGr.get(request.pathParams.sys_id)) {
        const brandKit = tableGr.getValue('brand_kit');
        let selectedBrandKit = {};
        if (brandKit) {
            const brandKitApi = new x_cadso_core.BrandKitAPI();
            selectedBrandKit = brandKitApi._getBrandKit({
                sysId: brandKit
            });
        }
        const sourcesArr = (tableGr.getValue('sources') + "").split(',');
        response.setBody({
            success: true,
            body: tableGr.getValue('json') ? JSON.parse(tableGr.getValue('json')) : {},
            brandKit,
            selectedBrandKit,
            sources: tableGr.getValue('sources'),
            sourcesArr,
            audienceTables: tableGr.getDisplayValue('audience_tables'),
            enableMergeTags: sourcesArr.length === 1
        });
    } else {
        response.setBody({
            success: false,
            body: {}
        });
    }

})(request, response);

