(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	try {
		const apiUtil = new x_cadso_automate.APIUtils();
		const table = 'x_cadso_core_merge_tag';
		const query = "sys_idISNOTEMPTY";
		const mergeTags = apiUtil.query({ table, query });

		if (!mergeTags) {
			return new sn_ws_err.NotFoundError('Could not find merge tags');
		}

		const allowedSysFields = [
			'sys_created_on',
			'sys_created_by',
			'sys_updated_on',
			'sys_updated_by'
		];

		const filtered = mergeTags.filter(tag => {
			const field = tag.field || '';
			return !(field.startsWith('sys_') && !allowedSysFields.includes(field));
		});

		response.setBody(filtered);
	} catch (error) {
		return new sn_ws_err.BadRequestError(error.message);
	}
	
})(request, response);

