(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	gs.info('NL - Calling MMS Image SSRAPI');
	try{
		const messageSend = new GlideRecordSecure('x_cadso_automate_message_send');
		const msgNoExt = request.pathParams.messageSendId.split('.').slice(0, -1).join('.');
		//if (messageSend.get(request.pathParams.messageSendId)) {
		gs.info('NL - msgNoExt: ' + msgNoExt);
		gs.info('NL - messageSend: ' + messageSend.get(msgNoExt));
		if (messageSend.get(msgNoExt)) {
			const mmsImage = new GlideRecordSecure('sys_attachment');
			if (mmsImage.get(messageSend.media)) {
				const hdrs = {};

				if (mmsImage.file_name.endsWith('jpg') || mmsImage.file_name.endsWith('jpeg')) {
					hdrs['Content-Type'] = 'image/jpeg';
				}
				else if (mmsImage.file_name.endsWith('png')) {
					hdrs['Content-Type'] = 'image/png';
				}
				else if (mmsImage.file_name.endsWith('gif')) {
					hdrs['Content-Type'] = 'image/gif';
				}

				// options for file download.
				//hdrs['Content-Type'] = 'application/octet-stream';
				//hdrs['Content-Disposition'] = 'attachment;filename=' + mmsImage.file_name;

				response.setStatus(200);
				response.setHeaders(hdrs);
				const gsa = new GlideSysAttachment();
				const attachmentStream = new gsa.getContentStream(mmsImage.sys_id);
				const writer = response.getStreamWriter();
				writer.writeStream(attachmentStream);
			}
		}
	}catch(error){
		return new sn_ws_err.BadRequestError(error.message);
	}
})(request, response);

