(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    const {
        sysId,
		table = 'x_cadso_cloud_page'
    } = request.body.data;
	const tableGR = new GlideRecordSecure(table);
	const encodedQuery = `sys_id=${sysId}`;

	let strat = {};

	tableGR.addEncodedQuery(encodedQuery, true);
	tableGR.orderBy('sys_created_on');
	tableGR.query();

    while (tableGR.next()) {
		strat = {
			name: tableGR.getDisplayValue('name'),
			title: tableGR.getDisplayValue('title'),
			url: tableGR.getDisplayValue('url'),
			meta: tableGR.getDisplayValue('meta'),
			thumbnail: {
				md: tableGR.getDisplayValue('thumbnail'),
				lg: tableGR.getDisplayValue('thumbnail_large')
			},
			headScript: tableGR.getDisplayValue('head_script'),
			bodyScript: tableGR.getDisplayValue('body_script'),
			html: tableGR.getDisplayValue('html'),
			sysId: tableGR.getUniqueValue()
		};
    }

    response.setBody({
        strat
    });

})(request, response);

