(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        const apiUtil = new x_cadso_automate.APIUtils();
        let table = 'x_cadso_journey_flow';
        const data = request.body.data;
        const journey = apiUtil.insert({
            table,
            data
        });
        journey.sysId = journey.sys_id;
        table = 'x_cadso_journey_version';
        apiUtil.insert({
            table,
            data: {
				...data,
				latest: true,
				version: 1,
				journey: journey.sys_id,
				short_description: data.name
            }
        });
        return journey;
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }

})(request, response);

