(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        const apiUtil = new x_cadso_automate.APIUtils();
        const table = 'x_cadso_journey_flow';
        const flowGR = new GlideRecord(table);

        if (!flowGR.get(request.pathParams.sys_id)) {
            return new sn_ws_err.NotFoundError('Could Not Find Journey');
        }

        if (flowGR.can_delete.nil()) {
            return new sn_ws_err.BadRequestError('Flow has been triggered atleast once and can not be deleted');
        }

        const sysID = request.pathParams.sys_id;
        const journeyDeleted = apiUtil.delete({
            table,
            sysID
        });

        if (!journeyDeleted) {
            return new sn_ws_err.NotFoundError('Could not delete Journey');
        }

        response.setStatus(204);
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }
})(request, response);

