(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    const journeyBuilder = new JourneyBuilder();
    const {
        triggers,
        actions,
        trigger,
        journey,
        steps,
        option,
        branches,
        recordType,
		plugins
    } = journeyBuilder.fetch({
        request,
        response
    });

    const {
        sysId,
        optionSysId,
        versionSysId = null
    } = request.pathParams;

    const journeyGr = new GlideRecord('x_cadso_journey_flow');
	const gdt = new GlideDateTime();

    if (!sysId || sysId === '-1' || !journeyGr.get(sysId)) {
        response.setBody({
            steps,
            actions,
            trigger,
            triggers,
            option,
            recordType,
            ...journey,
            branches,
			timeOffset: gdt.getDSTOffset(),
			plugins
        });

        return response;
    }

    response.setBody({
        steps,
        actions,
        trigger,
        triggers,
        option,
        recordType,
        ...journey,
        branches,
		timeOffset: gdt.getDSTOffset(),
		plugins
    });

    return response;

})(request, response);

