(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    const apiUtil = new x_cadso_automate.APIUtils();
    let table, query, order, orderBy;

    try {
		const journey = apiUtil.get({ table: 'x_cadso_journey_flow', sysID: request.pathParams.sysID});
		if(!journey){
			return new sn_ws_err.BadRequestError('Could not find Journey');
		}

        table = 'x_cadso_journey_version';
        query = 'journey=' + request.pathParams.sysID;
        order = 'orderByDesc';
        orderBy = 'version';

        const version = apiUtil.queryFirstOrLast({ table, query, order, orderBy });
        if (!version) {
            return journey;
        }
        table = 'x_cadso_journey_version_step';
        query = 'version=' + version.sys_id;
		let returnFields = ['short_description', 'description', 'action', 'action$DISPLAY', 'action.icon', 'trigger', 'conditions', 'date', 'datetime', 'day_of_month', 'day_of_week', 'description', 'duration', 'fields_values', 'journey', 'parents', 'children', 'table', 'table$DISPLAY', 'time', 'sys_id', 'version', 'configurations', 'option'];
        let steps = apiUtil.query({ table, query, returnFields });
		steps.sort((stepA, stepB) => stepA.order - stepB.order);
	// 	steps.map(step => {
	// 		step.table = {displayValue: step.table, value: step.table},
	// 		step.action = {displayValue: step.action$DISPLAY, value: step.action},
	// 		step.conditions = {displayValue: step.conditions, value: step.conditions}
	// })
		let journeyResponse = {
			journey: journey,
			version: version,
			versionSteps: steps
		};
        response.setBody(journeyResponse);
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }
})(request, response);

