(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    const apiUtil = new x_cadso_automate.APIUtils();
    let table, query;
    try {
        table = 'x_cadso_journey_version';
        query = 'journey=' + request.pathParams.journeySysID + '^version=' + request.pathParams.versionNumber;
		let includeDisplay = request.queryParams.includeDisplay == 'true';
        let version = apiUtil.query({ table, query, includeDisplay });
		version = version[0];
        if (!version) {
            return new sn_ws_err.BadRequestError('Could not find the provided Journey version');
        }
        table = 'x_cadso_journey_version_step';
        query = 'version=' + version.sys_id;
        const steps = apiUtil.query({ table, query });
		steps.sort((stepA, stepB) => stepA.order - stepB.order);
		let journeyVersion = {
			version: version,
			versionSteps: steps
		};
        return journeyVersion;
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }
})(request, response);

