(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    try {
        const apiUtil = new x_cadso_automate.APIUtils();
        const table = 'x_cadso_journey_flow';
        const sysID = request.pathParams.sys_id;
        const updates = request.body.data;

        if (updates.outdated_action !== 'continue') {
			// Bad way to do this but will stop all steps from firing if archived or just the latest for all other
            let instanceStepGQ = new global.GlideQuery('x_cadso_journey_instance_step')
                .where('journey', sysID)
                .where(new global.GlideQuery().where('status', 'Pending').orWhere('status', ''));
			if (updates.state !== '333') {
                instanceStepGQ.where('instance.latest', true);
            }
            instanceStepGQ.updateMultiple({
                status: 'Cancelled'
            });
        }

        if (updates.outdated_action) {
            delete updates.outdated_action;
        }

        const journey = apiUtil.update({
            table,
            sysID,
            updates
        });
        return journey;
    } catch (error) {
        return new sn_ws_err.BadRequestError(error.message);
    }

})(request, response);

