(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try{
		let has_feature = false;
		let feature_name = request.pathParams.featureName;
		const featureAPI = new x_cadso_work.FeatureAvailability();

		let lead_terms = ['lead', 'leads', 'sn_lead_mgmt_core_lead'];
		let consumer_terms = ['consumer', 'consumers', 'csm_consumer'];
		let contact_terms = ['contact', 'contacts', 'customer_contact'];

		if (lead_terms.includes(feature_name)) {
			has_feature = featureAPI.isLeadEnabled();
		}
		else if (consumer_terms.includes(feature_name)) {
			has_feature = featureAPI.isConsumerEnabled();
		}
		else if (contact_terms.includes(feature_name)) {
			has_feature = featureAPI.isContactEnabled();
		}

		response.setStatus(200);
		response.setHeader('Content-Type', 'application/json');
		response.setBody({ has_feature: has_feature });
		
	} catch(error){
		return new sn_ws_err.BadRequestError(error.message);
	}

})(request, response);

