operation_script.value:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
try {
    let {
        field,
		value
    } = request.body.data;
	
	const refTable = field.field.reference;
	const refQuery = field.field.referenceQuery;
	const refValue = field.value;
	field.refValid = false;
    var newBody = {
        field,
        success: false
    };
	
    var displayField = "";
    var isValid = false;
	
    if (refTable) {
        var dictionaryGR = new GlideRecord('sys_dictionary');
        dictionaryGR.addQuery("name", refTable);
        dictionaryGR.orderByDesc("sys_updated_on");
        dictionaryGR.addQuery("display", true);
        dictionaryGR.query();

        if (dictionaryGR.next()) {
            displayField = dictionaryGR.element.getValue();
        }
        if (displayField && refValue) {
            var refGR = new GlideRecordSecure(refTable);
            refGR.addEncodedQuery(refQuery);
            refGR.addQuery(displayField, refValue)
			// Handle sys id when option is clicked
			.addOrCondition('sys_id', refValue);
            refGR.query();

            if (refGR.next()) {
                if (refGR.isValidRecord()) {
                    isValid = true;
                }
            }
        }

        if (isValid) {
            newBody.field.refValid = isValid;
            newBody.success = true;
            newBody.message = "Reference validated for table: " + refTable;
        } else {
			newBody.field.refValid = isValid;
			newBody.success = true;
            newBody.message = "Could Not Validate Reference for table: " + refTable + " with value: " + refValue; 
		}
    } else {
        newBody.message = "Invalid Reference Table provided: " + refTable;
    }

    response.setBody(newBody);
} catch(e){
	gs.info("TENON FREEFORM VALIDATE ERROR: " + e.message);
}
})(request, response);

