(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	const { table, limit, query, orderBy, childTable, parentField, childQuery, childQueryComplete, childQueryTotal } = request.body.data;

	const items = [];
	let count = 0;

	const tableGr = new GlideRecordSecure(table);
	if (query) {
		tableGr.addEncodedQuery(query);
	}
	tableGr.orderByDesc(orderBy);
	tableGr.query();
	while(tableGr.next() && count < limit) {
		count++;
		const item = {
			displayValue: tableGr.getDisplayValue(),
			sysId: tableGr.getUniqueValue(),
			complete: 0,
			total: 0
		};
		
		// complete
		let childTableGA = new GlideAggregate(childTable);
		if (childQuery) {
			childTableGA.addEncodedQuery(childQuery, true);
		}
		if (parentField) {
			childTableGA.addQuery(parentField, tableGr.getUniqueValue());
		}
		if (childQueryComplete) {
			childTableGA.addEncodedQuery(childQueryComplete, true);
		}
		childTableGA.addAggregate('COUNT');
		childTableGA.query();
		while(childTableGA.next()) {
			item.complete = parseInt(childTableGA.getAggregate('COUNT'));
		}
		
		// total
		childTableGA = new GlideAggregate(childTable);
		if (childQuery) {
			childTableGA.addEncodedQuery(childQuery, true);
		}
		if (parentField) {
			childTableGA.addQuery(parentField, tableGr.getUniqueValue());
		}
		if (childQueryTotal) {
			childTableGA.addEncodedQuery(childQueryTotal, true);
		}
		childTableGA.addAggregate('COUNT');
		childTableGA.query();

		while(childTableGA.next()) {
			item.total = parseInt(childTableGA.getAggregate('COUNT'));
		}
		
		items.push(item);
	}

	response.setBody({ items });

})(request, response);

