(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {


    const {
        view,
        table
    } = request.body.data;

    let fields = [];
    var sysUiViewGr = new GlideRecord('sys_ui_view');
    sysUiViewGr.addEncodedQuery(`name=NULL^ORname=${view}^ORDERBYDESCname`);
    sysUiViewGr.setLimit(1);
    sysUiViewGr.query();

    while (sysUiViewGr.next()) {
        var sysUiListGr = new GlideRecord('sys_ui_list');
        sysUiListGr.addQuery('name', table);
        sysUiListGr.addQuery('view', sysUiViewGr.getUniqueValue());
        sysUiListGr.addEncodedQuery('sys_user=NULL'); // ^ORsys_userDYNAMIC90d1921e5f510100a9ad2572f2b477fe'); // This gets the users configuration but disabled for now
        sysUiListGr.orderByDesc('sys_user');
        sysUiListGr.setLimit(1);
        sysUiListGr.query();

        while (sysUiListGr.next()) {
            var sysUiListElement = new GlideRecord('sys_ui_list_element');
            sysUiListElement.addQuery('list_id', sysUiListGr.getUniqueValue());
            sysUiListElement.orderBy('position');
            sysUiListElement.query();

            while (sysUiListElement.next()) {
                fields.push(sysUiListElement.getValue('element'));
            }
        }
    }

    let data = {
        message: 'Success!',
        statusCode: 200,
        fields,
    };

    data.fields = fields;

    response.setStatus(data.statusCode);
    response.setBody(data);
    return response;

})(request, response);

