operation_script.value:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	const { user } = request.body.data;

	var sysUserGr = new GlideRecordSecure('sys_user');
	sysUserGr.addQuery('name', 'CONTAINS', user)
	.addOrCondition('user_name', 'CONTAINS', user)
	.addOrCondition('email', 'CONTAINS', user);
	sysUserGr.addActiveQuery();
	sysUserGr.setLimit(5);
	sysUserGr.query();
	const users = [];

	while (sysUserGr.next()) {
		let avatar = sysUserGr.getValue('avatar');
		avatar = avatar == null ? '' : avatar;
		let photo = sysUserGr.getValue('photo');
		photo = photo == null ? '' : photo;
		const userAvatar = avatar || photo;
		
		users.push({
			title: sysUserGr.getDisplayValue('title'),
			displayValue: sysUserGr.getDisplayValue(),
			value: sysUserGr.getUniqueValue(),
			avatar: userAvatar + '.iix'
		});
	}

	response.setBody({users});

	return response;

})(request, response);

