(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	const { table, sysId, state: newState } = request.body.data;
	let status = '';
	let statusValue = '';
	let state = '';
	let updated = false;
	const updatedFields = [];
	const task = {};

	const tableGr = new GlideRecordSecure(table);
	if (tableGr.get(sysId)) {
		if (table === 'x_cadso_work_task') {
			var workStatusGr = new GlideRecord('x_cadso_work_status');
			workStatusGr.addQuery('state', newState);
			workStatusGr.query();

			if (workStatusGr.next()) {
				tableGr.setValue('status', workStatusGr.getUniqueValue());
			}
		} else {
			tableGr.setValue('state', newState);
		}
		updated = tableGr.update();
	}

	if (updated) {
		if (table === 'x_cadso_work_task') {
			task.status = tableGr.getDisplayValue('status');
			updatedFields.push('status');
			task.statusValue = tableGr.getValue('status');
			updatedFields.push('statusValue');
		} else {
			task.status = tableGr.getDisplayValue('state');
			updatedFields.push('status');
			task.statusValue = tableGr.getValue('state');
			updatedFields.push('statusValue');
		}

		task.state = tableGr.getDisplayValue('state');
		updatedFields.push('state');
		task.stateValue = tableGr.getValue('state');
		updatedFields.push('stateValue');
	}

	const responseBody = {
		sysId,
		updatedFields,
		task,
		updated
	};

	response.setBody(responseBody);

})(request, response);

