(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	
	const table = request.queryParams.table + ''; // i.e. x_cadso_work_task
	const query = request.queryParams.query + '';
	const queries = request.queryParams.queries + ''; // i.e. active=true^state IN 70
	const queryList = queries.split('$');
	
	const results = [];

	queryList.forEach((_query) => {
		const ga = new GlideAggregate(table);
		if (query) {
			ga.addEncodedQuery(query, true);
		}
		if (_query) {
			ga.addEncodedQuery(_query, true);
		}
		ga.addAggregate('COUNT');
		ga.query();

		let count = 0;
		if (ga.next()) {
			results.push({
				query: _query,
				count: ga.getAggregate('COUNT')
			});
		} else {
			results.push({
				query: _query,
				count: 0
			});
		}
	});

	response.setBody(results);

})(request, response);

