(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
        let {
            initialize,
            title,
            table,
            field,
            type,
            sysId,
            conditions,
            filters = []
        } = request.body.data;
        var autoAudienceGr = new GlideRecord('x_cadso_automate_audience');
        if (initialize === 'true' || initialize === true) {
            autoAudienceGr.initialize();
            autoAudienceGr.setValue('field', field);
            autoAudienceGr.setValue('name', title);
            autoAudienceGr.setValue('table', table);
            autoAudienceGr.setValue('type', type);
            autoAudienceGr.setValue('filter_object', JSON.stringify(filters));
			autoAudienceGr.insert();
            sysId = autoAudienceGr.getUniqueValue();

            response.setBody({
                valid: !!sysId,
                sysId,
                table,
                field,
                view: autoAudienceGr.getValue('view')
            });

        } else if (autoAudienceGr.get(sysId)) {
            var Audience = new x_cadso_automate.Audience();
            const recordField = Audience.getRecordFields(table);

            let apiUtils = new x_cadso_automate.APIUtils();
            let count = apiUtils.count({
                table,
                query: conditions
            });
            
            autoAudienceGr.setValue('conditions', conditions);
            autoAudienceGr.setValue('count', count);
            autoAudienceGr.setValue('filter_object', JSON.stringify(filters));
			autoAudienceGr.setValue('hash_state', 'generating');
            autoAudienceGr.update();

            response.setBody({
                valid: true,
                sysId: sysId,
                table,
                field,
                recordField,
                conditions,
                count,
                view: autoAudienceGr.getValue('view')
            });
        }
        return response;


})(request, response);

